option.value:
@echo off
chcp 65001 >nul
echo ========================================
echo ️ LIMPIEZA COMPLETA DEL PROYECTO
echo ========================================
echo.
echo ⚠️ ADVERTENCIA: Se eliminarán archivos obsoletos
echo    y se reorganizará la estructura del proyecto
echo.
echo Presiona cualquier tecla para continuar o Ctrl+C para cancelar...
pause >nul

cd /d "%~dp0"

echo.
echo ========================================
echo PASO 1: Crear estructura de carpetas
echo ========================================
if not exist "docs" mkdir docs
if not exist "docs\database" mkdir docs\database
if not exist "docs\testing" mkdir docs\testing
if not exist "docs\sprints" mkdir docs\sprints
if not exist "docs\archive" mkdir docs\archive
echo ✅ Carpetas creadas

echo.
echo ========================================
echo PASO 2: Mover archivos SQL
echo ========================================
if exist "ACTUALIZAR_BASE_DATOS.sql" (
    move "ACTUALIZAR_BASE_DATOS.sql" "docs\database\" >nul
    echo ✅ Movido: ACTUALIZAR_BASE_DATOS.sql
)
if exist "ACTUALIZAR_BD_CITAS.sql" (
    move "ACTUALIZAR_BD_CITAS.sql" "docs\database\" >nul
    echo ✅ Movido: ACTUALIZAR_BD_CITAS.sql
)
if exist "api-backend\crear_tabla_horarios.sql" (
    move "api-backend\crear_tabla_horarios.sql" "docs\database\" >nul
    echo ✅ Movido: crear_tabla_horarios.sql
)
if exist "api-backend\EJECUTAR_ACTUALIZACION.sql" (
    move "api-backend\EJECUTAR_ACTUALIZACION.sql" "docs\database\" >nul
    echo ✅ Movido: EJECUTAR_ACTUALIZACION.sql
)

echo.
echo ========================================
echo PASO 3: Mover archivos de pruebas
echo ========================================
if exist "PRUEBAS_SPRINT1.md" (
    move "PRUEBAS_SPRINT1.md" "docs\testing\" >nul
    echo ✅ Movido: PRUEBAS_SPRINT1.md
)
if exist "PRUEBAS_SPRINT2.md" (
    move "PRUEBAS_SPRINT2.md" "docs\testing\" >nul
    echo ✅ Movido: PRUEBAS_SPRINT2.md
)
if exist "SPRINT2_COMPLETADO.md" (
    move "SPRINT2_COMPLETADO.md" "docs\sprints\" >nul
    echo ✅ Movido: SPRINT2_COMPLETADO.md
)

echo.
echo ========================================
echo PASO 4: Archivar documentos completados
echo ========================================
if exist "ANALISIS_ALINEACION_DIAGRAMAS.md" (
    move "ANALISIS_ALINEACION_DIAGRAMAS.md" "docs\archive\" >nul
    echo ✅ Archivado: ANALISIS_ALINEACION_DIAGRAMAS.md
)
if exist "SOLUCION_REGISTRO.md" (
    move "SOLUCION_REGISTRO.md" "docs\archive\" >nul
    echo ✅ Archivado: SOLUCION_REGISTRO.md
)
if exist "SISTEMA_CITAS_AUTOMATICO.md" (
    move "SISTEMA_CITAS_AUTOMATICO.md" "docs\archive\" >nul
    echo ✅ Archivado: SISTEMA_CITAS_AUTOMATICO.md
)

echo.
echo ========================================
echo PASO 5: Eliminar archivos obsoletos
echo ========================================
if exist "ARCHIVOS_DUPLICADOS_ELIMINAR.md" (
    del "ARCHIVOS_DUPLICADOS_ELIMINAR.md" >nul
    echo ✅ Eliminado: ARCHIVOS_DUPLICADOS_ELIMINAR.md
)
if exist "INSTRUCCIONES_LOGIN.txt" (
    del "INSTRUCCIONES_LOGIN.txt" >nul
    echo ✅ Eliminado: INSTRUCCIONES_LOGIN.txt
)
if exist "api-backend\ejecutar-crear-horarios.bat" (
    del "api-backend\ejecutar-crear-horarios.bat" >nul
    echo ✅ Eliminado: api-backend\ejecutar-crear-horarios.bat
)
if exist "api-backend\cargar-tramites.js" (
    del "api-backend\cargar-tramites.js" >nul
    echo ✅ Eliminado: api-backend\cargar-tramites.js
)
if exist "api-backend\crear-horarios.js" (
    del "api-backend\crear-horarios.js" >nul
    echo ✅ Eliminado: api-backend\crear-horarios.js
)
if exist "ARCHIVOS_A_ELIMINAR.md" (
    del "ARCHIVOS_A_ELIMINAR.md" >nul
    echo ✅ Eliminado: ARCHIVOS_A_ELIMINAR.md
)
if exist "LIMPIAR_PROYECTO.bat" (
    del "LIMPIAR_PROYECTO.bat" >nul
    echo ✅ Eliminado: LIMPIAR_PROYECTO.bat (antiguo)
)

echo.
echo ========================================
echo PASO 6: Limpiar build de Gradle
echo ========================================
if exist "gradlew.bat" (
    call gradlew.bat clean >nul 2>&1
    echo ✅ Gradle clean ejecutado
)

echo.
echo ========================================
echo ✅ LIMPIEZA COMPLETADA EXITOSAMENTE
echo ========================================
echo.
echo  Nueva estructura:
echo    docs\
echo    ├── database\     (archivos SQL)
echo    ├── testing\      (pruebas)
echo    ├── sprints\      (documentación sprints)
echo    └── archive\      (docs antiguos)
echo.
echo  Resumen:
echo    - Archivos eliminados: 7
echo    - Archivos movidos: 9
echo    - Proyecto organizado: ✅
echo.
echo ⚠️ IMPORTANTE: Revisa la carpeta docs\ para verificar
echo.
echo ========================================
echo Este script se auto-eliminará en 3 segundos...
echo ========================================
timeout /t 3 >nul

rem Auto-eliminar este BAT
(goto) 2>nul & del "%~f0"
